import { useEffect, useState } from "react";
import "./satellite.css";
const Satellite = (props) => {
  const [styleSlider, setStyleSlider] = useState({animationDelay:"0s"});
  const [color , setColor] = useState({background: "radial-gradient(at 50% 50%, white, black, grey, black, black)"});
  useEffect(() => {
    switch (props.index) {
      case "1":
        return (()=>{
          setStyleSlider({animationDelay:"0s"});
          setColor({background: "radial-gradient(at 50% 50%, white, black, grey, black, black)"})
        })
      case "2":
        return (()=>{
          setStyleSlider({animationDelay:"-2s"});
          setColor({background: "radial-gradient(at 50% 50%, white, blue, grey, black, black)"})
        })
    }
  }, []);
  return (
    <>
      <div class="ball">
        <div class="layer moving">
          <div class="layer gridplane xline"></div>
          <div class="layer gridplane xline2"></div>
          <div class="layer gridplane yline"></div>
          <div class="layer gridplane zline"></div>
          <div class="layer gridplane laser"></div>
          <div class="layer gridplane laser2"></div>
        </div>
        <div class="layer clip" style={styleSlider}>
          <div class="shade" style={color}></div>
        </div>
      </div>
    </>
  );
};
export default Satellite;
