import { useEffect, useState } from "react";
import "./satellite.css";
const Satellite = (props) => {
  const [styleSlider, setStyleSlider] = useState({ animationDelay: "0s" , boxShadow: "0px 0px 50px 0px #ffff"});
  const [color, setColor] = useState({
    background: "radial-gradient(at 50% 50%, white, black, grey, black, black)",
  });
  useEffect(() => {
    switch (props.index) {
      case "1":
        return () => {
          setStyleSlider({ animationDelay: "0s" , boxShadow: "0px 0px 50px 0px gold"});
          setColor({
            background:
              "radial-gradient(at 50% 50%, white, gold, rgb(240,230,140), rgb(184,134,11), rgb(184,134,11))",
          });
        };
        break;
      case "2":
        return () => {
          setStyleSlider({ animationDelay: "-2s" , boxShadow: "0px 0px 50px 0px rgb(0,191,255)" });
          setColor({
            background:
              "radial-gradient(at 50% 50%, white, rgb(0,191,255), rgb(0,191,255), rgb(135,206,250), rgb(135,206,250))",
          });
        };
        break;
      case "3":
        return () => {
          setStyleSlider({ animationDelay: "-4s" });
          setColor({
            background:
              "radial-gradient(at 30% 70%, white, red, rgb(255,69,0), rgb(250,128,114), rgb(250,128,114))",
          });
        };
        break;
    }
  }, []);
  return (
    <>
      <div class="ball">
        <div class="layer moving">
          <div class="layer gridplane xline"></div>
          <div class="layer gridplane xline2"></div>
          <div class="layer gridplane yline"></div>
          <div class="layer gridplane zline"></div>
          <div class="layer gridplane laser"></div>
          <div class="layer gridplane laser2"></div>
        </div>
        <div class="layer clip" style={styleSlider}>
          <div class="shade" style={color}></div>
        </div>
      </div>
    </>
  );
};
export default Satellite;
